<script>
    document.getElementById('resendVerificationForm').addEventListener('submit', function(event) {
        event.preventDefault();

        fetch(this.action, {
            method: 'POST',
            headers: {
                'X-CSRF-TOKEN': document.querySelector('meta[name="csrf-token"]').getAttribute('content')
            }
        })
        .then(response => {
            if (response.ok) {
                alert('Verification email sent successfully.');
            } else {
                throw new Error('Failed to resend verification email. Try after 2 min');
            }
        })
        .catch(error => {
            console.error(error);
            alert('Failed to resend verification email. Please try again later.');
        });
    });
    
    function showManualForm() {
        document.getElementById('manualWhatsAppForm').classList.remove('hidden');
